import React from 'react';

const SimplePage = () => {
    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 py-12 px-4 sm:px-6 lg:px-8">
            <div className="max-w-3xl mx-auto text-center">
                {/* Заголовок с анимацией */}
                <h1 className="text-4xl sm:text-5xl font-bold text-gray-800 mb-6 animate-fade-in">
                    Добро пожаловать!
                </h1>

                {/* Основной текст */}
                <p className="text-lg text-gray-600 mb-8">
                    Это простая страница, созданная с помощью React и Tailwind CSS.
                    Tailwind позволяет быстро создавать красивые интерфейсы без написания CSS.
                </p>

                {/* Интерактивная кнопка */}
                <button className="px-6 py-3 bg-indigo-600 text-white font-medium rounded-lg shadow-md hover:bg-indigo-700 transition-all duration-300 hover:scale-105 mb-12">
                    Начать работу
                </button>

                {/* Карточка с эффектом при наведении */}
                <div className="bg-white p-6 rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 transform hover:-translate-y-1">
                    <h2 className="text-2xl font-semibold text-gray-800 mb-3">О проекте</h2>
                    <p className="text-gray-600">
                        Этот пример показывает, как легко создавать современные интерфейсы с Tailwind.
                        Попробуйте изменить цвета или добавить новые компоненты!
                    </p>
                    <div className="mt-4 flex space-x-3">
            <span className="inline-block px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm">
              React
            </span>
                        <span className="inline-block px-3 py-1 bg-purple-100 text-purple-800 rounded-full text-sm">
              Tailwind
            </span>
                    </div>
                </div>

                {/* Футер */}
                <footer className="mt-12 text-gray-500 text-sm">
                    © {new Date().getFullYear()} Мой проект. Все права защищены.
                </footer>
            </div>
        </div>
    );
};

export default SimplePage;
