import React from 'react';
import mailIcon from '../assets/mail.png'
import vkIcon from '../assets/vk.png'
import instIcon from '../assets/inst.png'

const socials = [
    { icon: vkIcon, url: 'https://vk.com', name: 'ВКонтакте' },
    { icon: instIcon, url: 'https://instagram.com', name: 'Instagram' }
];

const SimplePage = () => {
    return (
        <header className="font-montserrat">
            <div className="container mx-auto py-3 bg-white text-[#999999] text-sm font-normal flex">
                <span className="mx-14">Ваш город: <span className="text-black font-medium">Москва</span></span>

                <nav className="space-x-7">
                    {[
                        { title: 'Про анонимность', url: '/about' },
                        { title: 'Доставка и оплата', url: '/delivery' },
                        { title: 'Условия возврата', url: '/returns' },
                        { title: 'FAQ', url: '/faq' }
                    ].map((item) => (
                        <a
                            key={item.url}
                            href={item.url}
                            className="
                                text-gray-600
                                hover:text-blue-700
                                transition-colors
                                duration-200
                                active:text-blue-900
                            "
                        >
                            {item.title}
                        </a>
                    ))}
                </nav>

                <button className="flex"><img className="mx-1 object-contain" src={mailIcon} alt="mail"/><span className="text-[#333333]">Задать вопрос</span></button>

                <div className="flex space-x-3 ml-4">
                    {socials.map((social) => (
                        <a
                            key={social.name}
                            href={social.url}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="
                                bg-gray-100
                                rounded-full
                                flex items-center justify-center
                                hover:bg-gray-200
                                transition-colors
                            "
                            aria-label={social.name}
                        >
                            <img
                                src={social.icon}
                                alt=""
                                className="w-4 h-4 object-contain"
                            />
                        </a>
                    ))}
                </div>
            </div>
        </header>
    );
};

export default SimplePage;
