import React from 'react';
import CatWoman from '../assets/images/cat_woman.png'
import CatMan from '../assets/images/cat_man.png'
import CatVib from '../assets/images/cat_vib.png'
import CatLub from '../assets/images/cat_lub.png'


const categories = [
    {name: 'Для женщин', image: CatWoman, url: '/category1'},
    {name: 'Для мужчин', image: CatMan, url: '/category2'},
    {name: 'Гигиена и косметика', image: CatLub, url: '/category3'},
    {name: 'Вибраторы', image: CatVib, url: '/category4'},
    {name: 'Лубриканты', image: CatLub, url: '/category5'},
    {name: 'Вибраторы', image: CatVib, url: '/category6'},
    {name: 'Гигиена и косметика', image: CatLub, url: '/category7'},
    {name: 'Вибраторы', image: CatVib, url: '/category8'}
];

const Category = ({className} ) => {
    return (
        <div className={`flex flex-col justify-center items-center mx-auto font-prata text-sm font-medium text-black ${className}`}>
            <h1 className="text-[28px]">Категории</h1>
            <div className="w-20 h-0.5 mt-5 bg-pink"></div>

            

        </div>
    );
};

export default Category;
