import React from 'react';
import mailIcon from '../assets/icons/mail.png'
import vkIcon from '../assets/icons/vk.png'
import instIcon from '../assets/icons/inst.png'
import logo from '../assets/images/logo.png'

const socials = [
    { icon: instIcon, url: 'https://instagram.com', name: 'Instagram' },
    { icon: vkIcon, url: 'https://vk.com', name: 'ВКонтакте' }
];

const SimplePage = () => {
    return (
        <header className="font-montserrat">

            <div className="border-b border-[#F5F5F5]">
                <div className="container mx-auto py-3 bg-white text-[#999999] text-sm font-normal flex justify-between items-center">
                    <div className="flex items-center">
                    <span className="mr-14">Ваш город: <span className="text-black font-medium">Москва</span>
                </span>
                        <nav className="space-x-7">
                            {[
                                { title: 'Про анонимность', url: '/about' },
                                { title: 'Доставка и оплата', url: '/delivery' },
                                { title: 'Условия возврата', url: '/returns' },
                                { title: 'FAQ', url: '/faq' }
                            ].map((item) => (
                                <a
                                    key={item.url}
                                    href={item.url}
                                    className="
                                    text-gray-600
                                    hover:text-blue-700
                                    transition-colors
                                    duration-200
                                    active:text-blue-900
                                "
                                >
                                    {item.title}
                                </a>
                            ))}
                        </nav>
                    </div>

                    <div className="flex items-center space-x-4">
                        <button className="flex items-center">
                            <img className="mx-2.5 object-contain" src={mailIcon} alt="mail" />
                            <span className="text-[#333333] mr-10 hover:text-blue-700">Задать вопрос</span>
                        </button>
                        <div className="flex space-x-5">
                            {socials.map((social) => (
                                <a
                                    key={social.name}
                                    href={social.url}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                    className="
                                    rounded-full
                                    flex items-center justify-center
                                    transition-colors
                                    "
                                    aria-label={social.name}
                                >
                                    <img
                                        src={social.icon}
                                        alt=""
                                        className="w-4 h-4 object-contain"
                                    />
                                </a>
                            ))}
                        </div>
                    </div>
                </div>
            </div>

            <div className="container mx-auto flex items-center bg-white py-3">
                <img className="mr-12" src={logo} alt="logo"/>
                <button className="bg-[#F593AB] w-33 h-12 rounded-md">Каталог</button>
            </div>
        </header>
    );
};

export default SimplePage;
